class SessionScanner {
  scanSessions(cliType, sessionsPath, projectPath) {
    const sessions = [];
    if (!sessionsPath || !projectPath) return sessions;

    try {
      if (!fs.existsSync(sessionsPath)) return sessions;

      // For Codex: scan date-layered structure YYYY/MM/DD/
      if (cliType === 'codex') {
        return this.scanCodexDateLayers(sessionsPath, projectPath);
      }

      return this.scanSessionFiles(cliType, sessionsPath, projectPath);
    } catch (error) {
      console.warn(`Warning: Could not scan ${cliType} sessions:`, error.message);
    }

    return sessions;
  }

  scanCodexDateLayers(sessionsPath, projectPath) {
    const sessions = [];

    try {
      // Scan year directories (YYYY)
      const years = fs.readdirSync(sessionsPath).filter(y => /^\d{4}$/.test(y));
      
      for (const year of years) {
        const yearPath = path.join(sessionsPath, year);
        
        try {
          // Scan month directories (MM)
          const months = fs.readdirSync(yearPath).filter(m => /^\d{2}$/.test(m));
          
          for (const month of months) {
            const monthPath = path.join(yearPath, month);
            
            try {
              // Scan day directories (DD)
              const days = fs.readdirSync(monthPath).filter(d => /^\d{2}$/.test(d));
              
              for (const day of days) {
                const dayPath = path.join(monthPath, day);
                sessions.push(...this.scanSessionFiles('codex', dayPath, projectPath));
              }
            } catch (error) {
              continue;
            }
          }
        } catch (error) {
          continue;
        }
      }
    } catch (error) {
      console.warn(`Warning: Could not scan Codex date layers:`, error.message);
    }

    return sessions;
  }

  scanSessionFiles(cliType, sessionsPath, projectPath) {
    const sessions = [];

    try {
      const files = fs.readdirSync(sessionsPath);
      for (const file of files) {
        if (file.endsWith('.jsonl')) {
          try {
            const filePath = path.join(sessionsPath, file);
            const content = fs.readFileSync(filePath, 'utf8');
            const lines = content.trim().split('\n').filter(line => line.trim());
            const messages = lines.map(line => JSON.parse(line));

            if (messages.length === 0) continue;

            const firstMsg = messages[0];
            const lastMsg = messages[messages.length - 1];
            const firstUserMsg = messages.find(m => m.type === 'user' || m.role === 'user');

            let title = 'Untitled Session';
            if (firstUserMsg) {
              const content = firstUserMsg.content || firstUserMsg.message || firstUserMsg.text;
              if (content) {
                title = content.substring(0, 100) || title;
              }
            }

            const sessionData = {
              sessionId: firstMsg.sessionId || firstMsg.id || file.replace('.jsonl', ''),
              title: title,
              messages: messages,
              timestamp: lastMsg.timestamp || lastMsg.createdAt || fs.statSync(filePath).mtime,
              cwd: firstMsg.cwd || firstMsg.workingDirectory,
              projectPath: firstMsg.cwd || firstMsg.workingDirectory,
              messageCount: messages.filter(m => m.type === 'user' || m.type === 'assistant' || m.role === 'user' || m.role === 'assistant').length
            };

            if (this.isProjectSession(sessionData, projectPath)) {
              sessions.push({
                cliType,
                sessionId: sessionData.sessionId,
                title: sessionData.title,
                content: this.extractContent(sessionData),
                updatedAt: new Date(sessionData.timestamp),
                messageCount: sessionData.messageCount,
                projectPath
              });
            }
          } catch (error) {
            console.warn(`Warning: Could not parse ${file}:`, error.message);
          }
        }
      }
    } catch (error) {
      console.warn(`Warning: Could not scan files:`, error.message);
    }

    return sessions;
  }

  scanAllCLISessions(projectPath) {
    const allSessions = [];
    const cliPaths = this.getCLISessionPaths();

    for (const [cliType, sessionsPath] of Object.entries(cliPaths)) {
      const sessions = this.scanSessions(cliType, sessionsPath, projectPath);
      allSessions.push(...sessions);
    }

    return allSessions;
  }

  getCLISessionPaths() {
    const homeDir = os.homedir();
    return {
      claude: path.join(homeDir, '.claude', 'sessions'),
      gemini: path.join(homeDir, '.gemini', 'sessions'),
      qwen: path.join(homeDir, '.qwen', 'projects'),
      iflow: path.join(homeDir, '.iflow', 'projects'),
      codebuddy: path.join(homeDir, '.codebuddy'),
      qodercli: path.join(homeDir, '.qodercli', 'sessions'),
      codex: path.join(homeDir, '.codex', 'sessions')
    };
  }

  isProjectSession(session, projectPath) {
    const sessionProject = session.projectPath || session.cwd;
    if (!sessionProject) return true;

    return sessionProject === projectPath ||
           sessionProject.startsWith(projectPath) ||
           projectPath.startsWith(sessionProject);
  }

  extractContent(sessionData) {
    if (sessionData.messages) {
      return sessionData.messages
        .map(msg => {
          if (msg.content) return msg.content;
          if (msg.message) return msg.message;
          if (msg.text) return msg.text;
          return '';
        })
        .join(' ');
    }
    return '';
  }
}

class SessionFilter {
  applyFilters(sessions, options, projectPath) {
    let filtered = sessions.filter(s => s.projectPath === projectPath);

    if (options.cli) {
      filtered = filtered.filter(s => s.cliType === options.cli);
    }

    if (options.search) {
      const term = options.search.toLowerCase();
      filtered = filtered.filter(s =>
        s.title.toLowerCase().includes(term) ||
        s.content.toLowerCase().includes(term)
      );
    }

    filtered.sort((a, b) => new Date(b.updatedAt) - new Date(a.updatedAt));

    if (options.limit) {
      filtered = filtered.slice(0, options.limit);
    }

    return filtered;
  }
}

